mapper.update:
UPDATE r_change
   SET r_choose_people = #{choosePeople,jdbcType=VARCHAR}
 WHERE r_del = 0
   AND r_name = #{name,jdbcType=VARCHAR}
   AND r_number = #{number,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM r_change
WHERE id != #{id}
AND r_name = #{name,jdbcType=VARCHAR}
AND r_number = #{number,jdbcType=VARCHAR}
AND r_del = 0
