mapper.select:
SELECT COUNT(*)
FROM r_change
WHERE r_del = 0

    AND r_name = #{name,jdbcType=VARCHAR}


    AND r_district = #{district,jdbcType=INTEGER}

SELECT id, r_name AS `NAME`, r_number AS `number`, r_area AS `AREA`, r_unit_price AS unitPrice, r_total_price AS totalPrice,r_choose_people AS choosePeople,r_district AS district FROM r_change WHERE r_del = 0
AND r_name = #{name,jdbcType=VARCHAR}
AND r_district = #{district,jdbcType=INTEGER}
ORDER BY id limit ${start},${pageSize}

mapper.update:
UPDATE r_change
   SET r_choose_people = #{choosePeople,jdbcType=VARCHAR}
 WHERE r_del = 0
   AND r_name = #{name,jdbcType=VARCHAR}
   AND r_number = #{number,jdbcType=VARCHAR}
