mapper.update:
update t_settle_accounts set deleted  = 1 ,
  modified_date = #{modifiedDate}, modified_user_id = #{modifiedUserId}
  where id = #{id}

mapper.select:
select
from t_settle_accounts where deleted = 0
and signing_status = #{signingStatus}
and (house_owner like concat('%',#{keywords} ,'%') or phone like concat('%',#{keywords} ,'%') or address like concat('%',#{keywords} ,'%'))
order by create_date desc
limit ${firstResult}, ${maxResults}

select count(1) from t_settle_accounts where deleted = 0
and signing_status = #{signingStatus}
and (house_owner like concat('%',#{keywords} ,'%') or phone like concat('%',#{keywords} ,'%') or address like concat('%',#{keywords} ,'%'))
